    public void cadastrar(){
       Cliente clie = new Cliente();
        
        clie.setNome(ctNomeClie.getText());
        clie.setCpf(ctCpfClie.getText());
        clie.setCidade(ctCidadeClie.getText());
        clie.setNascimento(ctNascClie.getText());
        clie.setEmail(ctEmailClie.getText());
        
        clienteController.inserirCliente(clie);
    }
