    private void btnLoginClieActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLoginClieActionPerformed
        String email = jTextField1.getText();
        if (email.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Por favor, insira seu email.");
        } else {
            if (clienteDAO.checkEmail(email)) {
                JOptionPane.showMessageDialog(this, "Login bem-sucedido!");
                this.setVisible(false);
                java.awt.EventQueue.invokeLater(new Runnable() {
                public void run() {
                    new AreaClie(email).setVisible(true);
                }});
            } else {
                JOptionPane.showMessageDialog(this, "Email n√£o encontrado.");
            }
        }
    }//GEN-LAST:event_btnLoginClieActionPerformed

